import React, {useState} from "react";

import ExpenseForm from "./ExpenseForm";
import "./NewExpense.css";

const NewExpense = (props) => {
  const [isOpne, setIsOpen] = useState(false) ;

  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    console.log(expenseData);
    props.onAddExprese(expenseData);
  };

  const closeExpenseFormHandler = (isCancel) => {
    setIsOpen(isCancel);
  };

  const clickAddNewExpenseHandler = () => {
    setIsOpen(false);
  };

  let addNewExpenseContent = (
    <button type="button" onClick={clickAddNewExpenseHandler}>
      Add New Expense
    </button>
  );

  if(isOpne){

    <ExpenseForm
    onSaveExpenseData={saveExpenseDataHandler}
    onCloseExpenseForm={closeExpenseFormHandler}
  />
  }


  return (
    <div className="new-expense">
    </div>
  );
};

export default NewExpense;
