import React, {useState} from "react";

import Card from "../UI/Card";
import styles from "./AddUser.module.css";

const VALID = 0 ;
const EMPTY_VALUE = 1 ;
const NAGATIVE_VALUE = 2 ;

const AddUser = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredAge, setEnteredAge] = useState("");

  const addUserHandler = (event) => {
    event.preventDefault();
  };

  const validHandler = (validCode) => {
    props.onValid(validCode);
  };

  const nameChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const ageChangeHandler = (event) => {
    setEnteredAge(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    if (enteredName.length === 0 || enteredAge.length === 0) {
      props.onSaveValid(EMPTY_VALUE);
      return;
    }

    if (enteredAge < 0) {
      props.onSaveValid(NAGATIVE_VALUE);
      return;
    }

    const userData = {
      username: enteredName,
      age: enteredAge,
      id: Math.random(),
    };

    props.onSaveUserData(userData);
    props.onSaveValid(VALID);
    setEnteredName("");
    setEnteredAge("");
  };

  return (
    <Card className={styles["new-user"]}>
      <form onSubmit={addUserHandler}>
        <div className={styles["new-user__controls"]}>
          <div className={styles["new-user__control"]}>
            <label htmlFor="username">Username</label>
            <input
              id="username"
              type="text"
              value={enteredName}
              onChange={nameChangeHandler}
            />
          </div>
          <div className={styles["new-user__control"]}>
            <label htmlFor="age">Age (Years)</label>
            <input
              id="age"
              type="number"
              value={enteredAge}
              onChange={ageChangeHandler}
            />
          </div>
        </div>
        <div className={styles["new-user__actions"]}>
          <button type="submit">Add User</button>
        </div>
      </form>
    </Card>
  );
};

export default AddUser;
