import React, { useState } from "react";

import AddUser from "./Components/Users/AddUser"
import UsersList from "./Components/Users/UsersList";
import Modal from "./Components/Valid/Modal";

const VALID = 0 ;
const EMPTY_VALUE = 1 ;
const NAGATIVE_VALUE = 2 ;

const App = () => {
  const [users, setUsers] = useState([]) ;
  const [isValid, setIsValid] = useState(VALID) ;

  const addUserHandler = (user) => {
    setUsers((prevUsers) => {
      return [user, ...prevUsers];
    });
  } ;

  const validHandler = (validCode) => {
    setIsValid(validCode) ;
    console.log(validCode) ;
  } ;

  return <div>
    <AddUser onAddUser={addUserHandler} onValid={validHandler}/>
    <UsersList people={users}/>
    <Modal validCode={isValid} onValid={validHandler}/>
  </div>;
};

export default App;
