import React from "react";

const VALID = 0 ;
const EMPTY_VALUE = 1 ;
const NAGATIVE_VALUE = 2 ;

const Error = (props) => {
    if (props.validCode === NAGATIVE_VALUE)
        return "Please enter a valid age (>0)." ;
    return "Please enter a valid name and age (non-empty value)." ;
};

export default Error;
