  }, [fetchMealsHandler]);

  if (meals.length > 0) {
    return (
      <section className={classes.meals}>
        <Card>
          <MealsList meals={meals} />
        </Card>
      </section>
    );
  }

  if (error) {
    return (
      <section className={classes.MealsError}>
        <p>{error}</p>
      </section>
    );
  }

  if (isLoading) {
    return (
      <section className={classes.MealsLoading}>
