import React, { useState, useEffect } from "react";

const useFetch = (taskActionFn, taskText = "") => {
  const fetchURL =
    "https://react-http-7adb5-default-rtdb.firebaseio.com/tasks.json";
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchTasks = async (taskText) => {
    setIsLoading(true);
    setError(null);
    try {
      let response;
      if (taskText === "") response = await fetch(fetchURL);
      else {
        response = await fetch(fetchURL, {
          method: "POST",
          body: JSON.stringify({ text: taskText }),
          headers: {
            "Content-Type": "application/json",
          },
        });
      }
      if (!response.ok) {
        throw new Error("Request failed!");
      }
      const data = await response.json();

      if (taskText === "") {
        const loadedTasks = [];
        for (const taskKey in data) {
          loadedTasks.push({ id: taskKey, text: data[taskKey].text });
        }

        taskActionFn(loadedTasks);
        // setTasks(loadedTasks);
      } else {
        const generatedId = data.name; // firebase-specific => "name" contains generated id
        const createdTask = { id: generatedId, text: taskText };

        taskActionFn(createdTask);
        //   props.onAddTask(createdTask);
      }
    } catch (err) {
      setError(err.message || "Something went wrong!");
    }
    setIsLoading(false);
  };

  useEffect(() => {
    if (taskText === "") fetchTasks();
  }, []);
  return [fetchTasks, isLoading, error];
};

export default useFetch;
