import React, { useState } from "react";
import useInput from "../hooks/useInput";
import Input from "./Input";

const BasicForm = (props) => {
  const firstName = useInput((value) => value.trim() !== "");

  const onSubmitFormHandler = (event) => {
    event.preventDefault();
    firstName.isValid
    firstName.reset() ;
  };

  return (
    <form onSubmit={onSubmitFormHandler}>
      <div className="control-group">
        <Input
          type="text"
          id="firstName"
          title="First Name"
          onChange={firstName.valueChangeHandler}
          onBlur={firstName.inputBlurHandler}
          hasError={firstName.hasError}
          errorText="first name must be not empty."
        />
        <div className={`form-control ${firstName.hasError && "invalid"}`}>
          <label htmlFor="firstName">First Name</label>
          <input
            type="text"
            id="firstName"
            onChange={firstName.valueChangeHandler}
            onBlur={firstName.inputBlurHandler}
          />
          {firstName.hasError && (
            <p className="error-text">must not be empty.</p>
          )}
        </div>
        <div className="form-control">
          <label htmlFor="lastName">Last Name</label>
          <input type="text" id="lastName" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="email">E-Mail Address</label>
        <input type="text" id="email" />
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
