import React, { useState } from "react";
import useInput from "../hooks/useInput";

const SimpleInput = (props) => {
  const name = useInput((value) => value.trim() !== "");

  // const [enteredElements, setEnteredElements] = useState({
  //   name: "",
  //   email: "",
  // });
  // const [enteredElementsTouched, setEnteredElementsTouched] = useState({
  //   name: false,
  //   email: false,
  // });
  // const enteredElementsIsValid = {
  //   name: enteredElements.name.trim() !== "",
  //   email: enteredElements.email.trim() !== "",
  //   emailCondition:
  //     enteredElements.email.trim() !== "" &&
  //     enteredElements.email.includes("@"),
  // };
  // const inputInvalid = {
  //   name: enteredElementsTouched.name && !enteredElementsIsValid.name,
  //   email: enteredElementsTouched.email && !enteredElementsIsValid.email,
  //   emailCondition:
  //     enteredElementsTouched.email &&
  //     enteredElementsIsValid.email &&
  //     !enteredElementsIsValid.emailCondition,
  // };

  let formIsValid = false;

  // if (Object.values(inputInvalid).every((enterdElement) => enterdElement))
  if (!name.hasError) formIsValid = true;

  const nameInputChangeHandler = (event) => {
    setEnteredElements((prevState) => {
      return { ...prevState, name: event.target.value };
    });
  };

  const nameInputBlurHandler = (event) => {
    setEnteredElementsTouched((prevState) => {
      return { ...prevState, name: true };
    });
  };

  const emailInputChangeHandler = (event) => {
    setEnteredElements((prevState) => {
      return { ...prevState, email: event.target.value };
    });
  };

  const emailInputBlurHandler = (event) => {
    setEnteredElementsTouched((prevState) => {
      return { ...prevState, email: true };
    });
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    // if (Object.values(inputInvalid).every((enteredElement) => !enteredElement))
    if(name.hasError)  
    return;

    console.log(name);

    // nameInputRef.current.value = '' ;
    // NOT IDEAL, DON'T MANIPULATE THE DOM
    setEnteredElements({ name: "", email: "" });
    setEnteredElementsTouched({ name: false, email: false });
  };

  const errorContent = (errorType) => (
    <p className="error-text ">${errorType}</p>
  );

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={`form-control ${inputInvalid.name && "invalid"}`}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredElements.name}
        />
        {inputInvalid.name && errorContent("Name must not be empty.")}
        <div
          className={`form-control ${
            (inputInvalid.email || inputInvalid.emailCondition) && "invalid"
          }`}
        >
          <label htmlFor="email">Your E-mail</label>
          <input
            type="email"
            id="email"
            onChange={emailInputChangeHandler}
            onBlur={emailInputBlurHandler}
            value={enteredElements.email}
          />
          {inputInvalid.email && errorContent("Email must not be empty.")}
          {inputInvalid.emailCondition &&
            errorContent("Email must include an '@'.")}
        </div>
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
