import React, { useState } from "react";
import useInput from "../hooks/useInput";
import Input from "./Input";

const BasicForm = (props) => {
  const firstName = useInput((value) => value.trim() !== "");
  const lastName = useInput((value) => value.trim() !== "");
  const email = useInput((value) => value.trim() !== "");

  let formIsValid = false;

  if (firstName.isValid) formIsValid = true;

  const onSubmitFormHandler = (event) => {
    event.preventDefault();
    if (!firstName.isValid) return;
    firstName.reset();
  };

  return (
    <form onSubmit={onSubmitFormHandler}>
      <div className="control-group">
        <Input
          type="text"
          id="firstName"
          title="First Name"
          onChange={firstName.valueChangeHandler}
          onBlur={firstName.inputBlurHandler}
          hasError={firstName.hasError}
          errorText="first name must be not empty."
        />
        <Input
          type="text"
          id="lastName"
          title="Last Name"
          onChange={lastName.valueChangeHandler}
          onBlur={lastName.inputBlurHandler}
          hasError={lastName.hasError}
          errorText="last name must be not empty."
        />
      </div>
      <div className="form-control">
        <label htmlFor="email">E-Mail Address</label>
        <input type="text" id="email" />
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
