import React, { useState } from "react";

const BasicForm = (props) => {
  const [enteredFirstName, setEnterdFirstName] = useState("");
  const [enteredFristNameTouched, setEnteredFristNameTouched] = useState(false);

  const FirstNameIsValid = enteredFirstName.trim() !== "" ;
  const FirstNameInvalid = FirstNameIsValid && !enteredFristNameTouched ;

  const onFirstNameChangeHandler = (event) => {
    setEnterdFirstName(event.current.value);
  };

  const onFirstNameBlurHandler = () => {
    setEnteredFristNameTouched(true) ;
  };

  const onSubmitFormHandler = () => {} ;

  return (
    <form>
      <div className="control-group">
        <div className={`form-control ${FirstNameInvalid && "invalid"}`}>
          <label htmlFor="firstName">First Name</label>
          <input
            type="text"
            id="firstName"
            onChange={onFirstNameChangeHandler}
            onBlur={onFirstNameBlurHandler}
          />
          {FirstNameInvalid && <p>must not be empty.</p>}
        </div>
        <div className="form-control">
          <label htmlFor="lastName">Last Name</label>
          <input type="text" id="lastName" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="email">E-Mail Address</label>
        <input type="text" id="email" />
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
