import React, { useState } from "react";

const SimpleInput = (props) => {
  const [enteredElement, setEnteredForm] = useState({ name: "" });
  const [enteredTouched, setEnteredTouched] = useState({ name: false });

  const enteredIsValid = { name: enteredForm.name.trim() !== "" };
  const inputInvalid = { name: enteredTouched.name && !enteredIsValid.name };

  let formIsValid = false;

  if (enteredIsValid.every((enterdElement) => enterdElement))
    formIsValid = true;

  const nameInputChangeHandler = (event) => {
    setEnteredForm((prevState) => {
      return { ...prevState, name: event.target.value };
    });
  };

  const nameInputBlurHandler = (event) => {
    setEnteredTouched((prevState) => {
      return { ...prevState, name: true };
    });
  };

  const emailInputChangeHandler = (event) => {
    setEnteredForm((prevState) => {
      return { ...prevState, email: event.target.value };
    });
  };

  const emailInputBlurHandler = (event) => {
    setEnteredTouched((prevState) => {
      return { ...prevState, email: true };
    });
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    if (!enteredIsValid.every((enteredElement) => enteredElement)) return;

    console.log(enteredElement);

    // nameInputRef.current.value = '' ;
    // NOT IDEAL, DON'T MANIPULATE THE DOM
    setEnteredName("");
    setEnteredNameTouched(false);
  };

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={`form-control ${nameInputInvalid && "invalid"}`}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
        />
        {nameInputInvalid && (
          <p className="error-text ">Name must not be empty.</p>
        )}

        <label htmlFor="email">Your Email</label>
        <input
          type="email"
          id="email"
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
        />
        {nameInputInvalid && (
          <p className="error-text ">Email must not be empty.</p>
        )}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
